      <b-row class="px-3 mt-3 d-block border-bottom pb-3"
        v-if="!user.isSubscribed && !isOwner && user.isCreator && !user.bundles.length > 0">
        <h5 class="mb-3">{{ $t("general.subscription") }}</h5>
        <b-button variant="primary" block @click.prevent="subscribe">
          {{
          $t("general.subscribe-for-x", [
          user.isFree ? $t("general.free") : user.priceFormatted,
          ])
          }}
        </b-button>
      </b-row>
      <b-row class="px-3 mt-3 d-block border-bottom pb-3" v-if="
      (!isOwner &&
      !user.isSubscribed &&
      !user.isFree &&
      user.isCreator &&
      user.bundles.length > 0) ">

        <div v-for="(item, index) in user.bundles" :key="index">
          <h5 v-if="item.months==1" class="mb-3">{{ $t("general.limited-offer", item.title(user))}}</h5>
          <b-button variant="primary" block @click.prevent="subscribe(item)" :key="index" v-if="item.months==1">
            {{ $t("general.get-x-months-for-y-z-off", item.title(user))}}
            <p style="display:none;">{{$t(hascamp=true)}}</p>
          </b-button>
          <h7 v-if="item.months==1" class="text-muted small">{{
          $t("general.regular-price", [
          user.isFree ? $t("general.free") : user.priceFormatted,
          ])
          }}</h7>
        </div>
        <b-row class="px-3 mt-3 d-block border-bottom pb-3" v-if="!hascamp">
          <h5 class="mb-3">{{ $t("general.subscription") }}</h5>
          <b-button variant="primary" block @click.prevent="subscribe">
            {{
            $t("general.subscribe-for-x", [
            user.isFree ? $t("general.free") : user.priceFormatted,
            ])
            }}
          </b-button>
        </b-row>
      </b-row>
      <b-row class="px-3 mt-3 d-block border-bottom pb-3" v-if="
