<script>
import Cursor from 'wavesurfer.js/dist/plugin/wavesurfer.cursor';

import Post from "../models/Post";
import User from "../models/User";
import UiPosts from "../ui/UiPosts.vue";
import Payment from "../models/Payment";
import UiUsername from "../ui/UiUsername.vue";
import UiVueShowMoreText from "../ui/UiVueShowMoreText.vue";
import UiAudioPlay from "../ui/UiAudioPlay.vue";

export default {
  components: { UiPosts, UiUsername, UiVueShowMoreText, UiAudioPlay },
  data: function () {
    return {
      user: {},
      hascamp: false,
      posts: [],
      scrollPosition: null,
      page: 1,
      hasMore: false,
      isLoading: false,
      rnd: Math.random(),
      postsType: Post.TYPE_ACTIVE,
      audioOptions: {
        plugins: [
          Cursor.create()
        ],

        waveColor: '#C6C7C8',
        progressColor: "#00AFF0",
        barWidth: 4,
        height: 50,
        responsive: true,
        hideScrollbar: true,
        barRadius: 4
      },
      audioFile: "https://d1r1kdez04i1xw.cloudfront.net/bio_voice/1665788983.mp3",
      audioProps: {
        duration: 0,
        currentTime: 0,
        isPlaying: false
      },
      isAudioLoaded: false,
      getCurrentTimeInterval: 0
    };
  },
  computed: {
    isScrolled: function () {
      return this.scrollPosition >= 180;
    },
    isOwner: function () {
      if (this.$store.state.currentUser) return this.user.id == this.$store.state.currentUser.id;
      else return false;
    },
    username: function () {
      return this.$route.params.username;
    },
    types: function () {
      return {
        active: Post.TYPE_ACTIVE,
        scheduled: Post.TYPE_SCHEDULED,
        expired: Post.TYPE_EXPIRED,
      };
    },
    currentDurationTime: function () {
      if (this.isAudioLoaded) {
        return this.$refs.surf.waveSurfer.getCurrentTime();
      }
      else {
        return 0
      }
    }
  },
  watch: {

    username: function (oldV, newV) {
      if (oldV && newV && oldV != newV) {
        this.reset();
        this.loadUser();
      }
    },
    currentDurationTime: {
      handler(val) {
        console.log(val)
      },
      deep: true
    }
  },
  mounted() {
    this.loadUser();
    window.addEventListener("scroll", this.updateScroll);
  },
  methods: {
    change(showAll) {
      console.log(showAll)
    },
    updatePosts(type) {
      this.postsType = type;
      this.reset();
      this.loadPosts();
    },
    reset() {
      this.isLoading = false;
      this.hasMore = false;
      this.page = 1;
      this.posts = [];
    },
    updateScroll() {
      this.scrollPosition = window.scrollY;
      const scrollPosition = window.innerHeight + window.scrollY;
      if (
        document.body.offsetHeight &&
        scrollPosition &&
        document.body.offsetHeight - scrollPosition <= 1000 &&
        !this.isLoading &&
        this.hasMore
      ) {
        this.loadMore();
      }
    },
    loadUser() {
      if (this.$store.state.token) {
        this.$get(
          "/users/" + this.username,
          (data) => {
            this.user = new User(data);
            this.loadPosts();
          },
          (errors) => {
            console.log(errors);
          }
        );
      } else {
        this.$get(
          "/users/guest/" + this.username,
          (data) => {
            this.user = new User(data);
            this.loadPosts();
          },
          (errors) => {
            console.log(errors);
          }
        );
      }

    },
    loadPosts() {
      this.isLoading = true;
      if (this.$store.state.token) {
        this.$get(
          "/posts/user/" +
          this.user.id +
          "?page=" +
          this.page +
          "&type=" +
          this.postsType,
          (data) => {
            let posts = [...this.posts];
            for (let obj of data.data) {
              posts.push(new Post(obj));
            }
            this.posts = posts;
            this.hasMore = data.next_page_url != null;
            this.isLoading = true;
          },
          (errors) => {
            console.log(errors);
          }
        );
      } else {
        this.$get(
          "/posts/guest/" +
          this.user.id +
          "?page=" +
          this.page +
          "&type=" +
          this.postsType,
          (data) => {
            let posts = [...this.posts];
            for (let obj of data.data) {
              posts.push(new Post(obj));
            }
            this.posts = posts;
            this.hasMore = data.next_page_url != null;
            this.isLoading = true;
          },
          (errors) => {
            console.log(errors);
          }
        );
      }

    },
    loadMore() {
      if (this.hasMore) {
        this.page = this.page + 1;
        this.loadPosts();
      }
    },
    copyLink() {
      const link = `${process.env.VUE_APP_APP_URL}${this.user.url}`;
      this.$copyText(link);
      this.$bvToast.toast(this.$t("general.link-copied"), {
        autoHideDelay: 2000,
        title: this.$t("general.link"),
        solid: true,
        toaster: "b-toaster-bottom-left",
      });
    },
    addToList(user) {
      if (this.$store.state.token) this.$store.state.addToListUser = user;
      else window.location.href = "/signup"
    },
    subscribe(bundle) {
      if (!this.$store.state.currentUser) {
        window.location.href = "/signup"
      } else if (this.user.isFree) {
        this.$post(
          "/subscribe/" + this.user.id,
          {},
          () => {
            this.reset();
            this.loadUser();
          },
          (errors) => {
            console.log(errors);
          }
        );
      } else {
        this.$buyItem({
          type: Payment.TYPE_SUBSCRIPTION_NEW,
          user: this.user,
          bundle: bundle,
        });
      }
    },
    tip() {
      if (this.$store.state.token) {
        this.$buyItem({
          type: Payment.TYPE_TIP,
          user: this.user,
        });
      } else {
        window.location.href = "/?next='/adminuser'"
      }

    },

    startPause() {
      if (this.$refs.surf.waveSurfer) {
        this.$refs.surf.waveSurfer.playPause()
        this.audioProps.isPlaying = this.$refs.surf.waveSurfer.isPlaying()
        if (this.audioProps.isPlaying) {
          this.getCurrentTimeInterval = setInterval(() => {
            this.audioProps.currentTime = this.$refs.surf.waveSurfer.getCurrentTime()
            console.log(this.audioProps.currentTime)
          }, 1000);
        }
        else {
          clearInterval(this.getCurrentTimeInterval)
          this.getCurrentTimeInterval = 0
        }
      }
    },
    audioComponentMounted() {
      this.isAudioLoaded = true;
      this.getAudioProps();
      setTimeout(() => {
        this.audioProps.duration = this.$refs.surf.waveSurfer.getDuration()
        this.$refs.surf.waveSurfer.on('finish', () => {
          this.$refs.surf.waveSurfer.stop()
          clearInterval(this.getCurrentTimeInterval)
          this.getCurrentTimeInterval = 0
          this.getAudioProps();
        })
      }, 1000);
    },
    secondToMin(second) {
      let parsedSec = Math.round(second)
      var minutes = Math.floor(parsedSec / 60);
      var seconds = parsedSec - minutes * 60;
      return `${minutes}:${seconds}`
    },

    getAudioProps() {
      this.audioProps.duration = this.$refs.surf.waveSurfer.getDuration()
      this.audioProps.currentTime = this.$refs.surf.waveSurfer.getCurrentTime()
      this.audioProps.isPlaying = this.$refs.surf.waveSurfer.isPlaying()
    }
  },
};
</script>